import React,{useState} from 'react'
import { Link } from 'react-router-dom';


const Q11 = () => {
 
    const [number,setnumber]=useState([]);
    const [isDisplay,setDisplay]=useState(true);
    const handleChange=(e)=>{
      setDisplay('');
      setnumber(e.target.value);
    }

      const maxFreq=(number, n)=>{
        setDisplay('');
        var res = 0;
        var count = 1;
        for (var i = 1; i < n; i++) {
          if (number[i] === number[res]) {
            count++;
          } else {
            count--;
          }
 
          if (count === 0) {
            res = i;
            count = 1;
          }
        }
 
        return number[res];
      }
 
   
      var n = number.length;
      var freq = maxFreq(number, n);
      var count = 0;
      for (var i = 0; i < n; i++) {
        if (number[i] === freq) {
          count++;
        }
      }
      

    return (
      <div className=" h-[80vh] flex justify-center items-center flex-col">
        <h4 className=' text-center font-semibold text-lg  text-red-400'>Most Occuring Elements in an numberay</h4>
        <input className=' border-2 border-yellow-400 p-1 mt-2 ' name='N' type="text" placeholder="Enter a number..."onChange={handleChange} /><br /><br />
        <button className=' border-2 border-yellow-400  -mt-8 p-2 rounded-lg hover:bg-slate-300 transition duration-200 ease-in-out hover:text-red-600' 
                onClick={maxFreq}>Check </button>
         <p>{isDisplay &&<div className='text-green-600 font-normal'> <a className=' text-indigo-600 uppercase font-semibold'> Result:</a>{isDisplay}</div>}</p>
        <button className='mt-4 p-1 border-2  rounded-lg bg-red-600 text-white font-semibold'><Link to='/'> Go to Home</Link></button>  
      </div>
  )
}

export default Q11
