import React,{useState} from 'react';
import { Link } from 'react-router-dom';

const Q1 = () => {

    const [string,setString]=useState('');
            const [isDisplay,setDisplay]=useState(true);
            const handleChange=(e)=>{
              setDisplay(false);
              setString(e.target.value);
            }
            const removeVowels=()=>{
              setDisplay(true);
              let temp=string;
              temp=temp.replace(/[aeiou]/gi, '');
              setString(temp);
            }
            return (
              <div >

                <input className=' border-2 border-yellow-400 p-1 ' type="text" placeholder="Enter a string..."onChange={handleChange} /><br /><br />
                <button className=' border-2 border-yellow-400  ml-10 p-2 rounded-lg hover:bg-slate-300 transition duration-200 ease-in-out hover:text-red-600' onClick={removeVowels}>Remove Vowels</button>
                <p>Removed vowels in string:{isDisplay && string}</p>
              <button><Link to='/'></Link> Go to Home</button>  
              </div>
            );
          }

export default Q1
