import React,{useState} from 'react'
import { Link } from 'react-router-dom';



const Q6 = () => {
    const [string,setString]=useState([]);
    const [isDisplay,setDisplay]=useState(true);
    const handleChange=(e)=>{
      setDisplay(false);
      setString(e.target.value);
    }
    const countlength=()=>{
      setDisplay(true);
      let count=string;
      count=count.length;
      setString(count);
    }
    return (
      <div className=" h-[80vh] flex justify-center items-center flex-col">
        <h4 className=' text-center font-semibold text-lg  text-red-400'>Number of words in string
</h4>
        <input className=' border-2 border-yellow-400 p-1 mt-2' type="text" placeholder="Enter a string..."onChange={handleChange} /><br /><br />
        <button className=' border-2 border-yellow-400  -mt-8 p-2 rounded-lg hover:bg-slate-300 transition duration-200 ease-in-out hover:text-red-600' 
                onClick={countlength}>Count length</button>
        <p>Count of String length:<h3 className=' text-blue-600 text-center'>{isDisplay && string}</h3></p>
        <button className='mt-4 p-1 border-2  rounded-lg bg-red-600 text-white font-semibold'><Link to='/'> Go to Home</Link></button>  
      </div>
    )
}

export default Q6
