const Q4 = () => {
    const [state,setState]=useState();
    const [isDisplay,setDisplay]=useState(true);
    const handleChange=(e)=>{
      setDisplay(false);
      setState(e.target.value);
    }
    const countVowels=(e)=>{
      setDisplay(true);
let number =parseInt(e.target.value)

if (number  >= 2){
    if(number == 2){
        isDisplay = 'this is prime number'
    }else{
        let prime = true
        for(let i =2 ; i < number; i++){
            prime=false;
            break
        }
    }
 if(prime){
     isDisplay='prime'
 }else{
     isDisplay='no tprime'
 }

 

}
  
    }
    return (
      <div className="">
        <input className=' border-2 border-yellow-400 p-1 ' type="text" placeholder="Enter a string..."onChange={handleChange} /><br /><br />
        <button className=' border-2 border-yellow-400  ml-10 p-2 rounded-lg hover:bg-slate-300 transition duration-200 ease-in-out hover:text-red-600' 
                onClick={countVowels}>Count Vowels</button>
        <b><p>{isDisplay && state}</p></b>
        
      </div>
    )
}

export default Q4
