import React,{useState} from 'react'


const Q3 = () => {
 
    const [array,setArray]=useState([]);
    const [isDisplay,setDisplay]=useState(true);
    const handleChange=(e)=>{
      setDisplay(false);
      setArray(e.target.value);
    }
    const matrixMultible=()=>{
      setDisplay(true);
      let temp=array[[2,5,6],[3,2,8],[2,4,1]];
      let newTemp=array[[2,5,6],[3,2,8],[2,4,1]];
      matrix=temp*newTemp
      setArray(matrix);
    }
    return (
      <div className="">
        <input className=' border-2 border-yellow-400 p-1 ' type="text" placeholder="Enter a string..."onChange={handleChange} /><br /><br />
        <button className=' border-2 border-yellow-400  ml-10 p-2 rounded-lg hover:bg-slate-300 transition duration-200 ease-in-out hover:text-red-600' 
                onClick={matrixMultible}>Count Vowels</button>
        <b><p>{isDisplay && string}</p></b>
        
      </div>
  )
}

export default Q3
