import { useState } from 'react'
import { Link } from 'react-router-dom';

const Q14 = () => {
    const [string, setString] = useState();
    const handleChange=(e)=>{
        setDisplay();
        setString(e.target.value);
      }
    const[isDisplay,setDisplay] = useState()
    const Frequency = () =>{
      const text= string
const textAsArray = text.split('').sort()
let charactersList = {}

for (string of textAsArray) {

   if (!charactersList[string]) {
    charactersList[string]=1;
   }
   else {
    charactersList[string]++
  }
}

console.log(charactersList) 
setDisplay(charactersList) 
    }
   
  return (
    <div className=" h-[80vh] flex justify-center items-center flex-col">
    <h4 className=' text-center font-semibold text-lg  text-red-400'>Count character frequency in a string</h4>
    <input className=' border-2 border-yellow-400 p-1 mt-2 ' type="text" placeholder="Enter a string ..."onChange={handleChange} /><br /><br />
    <button className=' border-2 border-yellow-400 p-2 -mt-8 rounded-lg hover:bg-slate-300 transition duration-200 ease-in-out hover:text-red-600' 
            onClick={Frequency}>Submit</button>
            <p>{isDisplay &&<div className='text-green-600 text-lg uppercase font-bold'>  Result:<a className='text-white  font-bold'>{isDisplay}</a></div>}</p>

    <button className='mt-4 p-1 border-2  rounded-lg bg-red-600 text-white font-semibold'><Link to='/'> Go to Home</Link></button>  
    
  </div>
    
  )
}

export default Q14
