import React,{useState} from 'react';
import { Link } from 'react-router-dom';

const Q16 = () => {

  const [InputNumber, setInputNumber] = useState({
        number1: [],
        number2: []
    })
            const [isDisplay,setDisplay]=useState(true);
            const handleChange=(e)=>{
              setDisplay('');
              setInputNumber(e.target.value);
            }
            const HCF=()=>{
              setDisplay('');
             
              // program to find the HCF or GCD of two integers

      let hcf;
 
 
  // looping from 1 to number1 and number2
   for (let i = 1; i <= number1 && i <= number2; i++) {

    // check if is factor of both integers
    if( number1 % i == 0 && number2 % i == 0) {
        hcf = i;
    }
}

// display the hcf
setDisplay(`HCF of ${number1} and ${number2} is ${hcf}.`);

    }

      return (
              <div  className=' w-full h-[80vh] flex justify-center items-center flex-col'>
                <h4 className=' text-center font-semibold text-lg text-red-400'> Highest Common Factor (HCF) or Greatest Common Divisor (GCD)</h4>
                <input className=' border-2 border-yellow-400 p-1 mt-4 '
                   type="text" placeholder="Enter a string..." name='number1' value={InputNumber.number1} onChange={handleChange} /><br />
                <input className=' border-2 border-yellow-400 p-1 mt-4 '
                   type="text" placeholder="Enter a string..." name='number2' value={InputNumber.number2} onChange={handleChange} />  <br /> 
                <button className=' border-2 border-yellow-400   p-2 rounded-lg hover:bg-slate-300 transition duration-200 ease-in-out hover:text-red-600 -mt-8' 
                    onClick={HCF}>Find HCF</button>
                    <p>{isDisplay &&<div className='text-green-600 font-normal'> <a className=' text-indigo-600 uppercase font-semibold'> Result:</a>{isDisplay}</div>}</p>
              <button className='mt-4 p-1 border-2  rounded-lg bg-red-600 text-white font-semibold'>
                <Link to='/'> Go to Home</Link>
              </button>  
              </div>
            );
          }

export default Q16
