        setDisplay(true);
// declaration of the variables  
var n1 = 0,  n2 = 1, next_num, i;  
var num = number 
document.write( "Fibonacci Series: ");  
for ( i = 1; i <= num; i++)  
{  setDisplay(" <br> " +  n1); // print the n1  
    next_num = n1 + n2; // sum of n1 and n2 into the next_num  
      
    n1 = n2; // assign the n2 value into n2  
    n2 = next_num; // assign the next_num into n2  
}

}
    return (
      <div className=" h-[80vh] flex justify-center items-center flex-col">
        <h4 className=' text-center font-semibold text-lg  text-red-400'>Fibonacci Series</h4>
        <input className=' border-2 border-yellow-400 p-1 mt-2 ' name='N' type="text" placeholder="Enter the number..."onChange={handleChange} /><br /><br />
        <button className=' border-2 border-yellow-400  -mt-8 p-2 rounded-lg hover:bg-slate-300 transition duration-200 ease-in-out hover:text-red-600' 
                onClick={numberCheck}>Check </button>
         <b><p>{isDisplay && isDisplay}</p></b> 
        <button className='mt-4 p-1 border-2  rounded-lg bg-red-600 text-white font-semibold'><Link to='/'> Go to Home</Link></button>  
      </div>
  )
}

export default Q8
